pragma solidity ^0.4.24;
contract Bank {
    struct Client {
        address _address; // address of client
        uint deposit;
    }

    address owner;
    mapping(address => Client) public clientList;
    uint clientCounter;

    constructor() public payable {
        require(msg.value == 30 ether, "Initial funding of 30 ether required for rewards");
        /* Set the owner to the creator of this contract */
        owner = msg.sender;
        clientCounter = 0;
    }

    function enroll() public {
        clientList[msg.sender].deposit = 5 ether;
        clientList[msg.sender]._address = msg.sender;
        clientCounter++;
    }

    function getClientCounter() public view returns(uint){
        return clientCounter;
    }

    function addDeposit() public payable {
        clientList[msg.sender].deposit += msg.value;
    }

    function withdraw(uint amount) public payable {
        if (clientList[msg.sender].deposit < amount){
            revert("not enough deposit to make the withdraw");
        }else {
            clientList[msg.sender].deposit -= amount;
            msg.sender.transfer(amount);
        }
    }

    function checkDeposit(address account) public view returns (uint) {
        return clientList[account].deposit;
    }

    function () public payable {

    }
}

contract Client {
    address owner; // this client's address
    Bank bank; // the bank that this client connected with

    constructor(address _referBank){
        owner = msg.sender;
        bank = Bank(_referBank);
    }

    function register() public {
        bank.enroll();
    }

    function addDeposit() public payable {
        bank.addDeposit();
    }

    function withdraw(uint amount) public payable{
        bank.withdraw(amount);
    }

    function checkDeposit(address account) public view returns (uint) {
        return bank.checkDeposit(account);
    }

    // function checkDeposit(address account) public view returns (uint) {
    //     return clientList[account].deposit;
    // }

    function () public payable {
        // owner.transfer(this.balance);
    }
}
